import React from 'react'
import ReactDOM from 'react-dom';

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {nombre: '', apellido: ''};


        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleInputChange(event) {
        const name = event.name;
        const apellido = event.apellido;
        this.setState({
            [name]: "nombre",
            [apellido]: "apellido"
        });
    }

    handleSubmit(event) {
        alert('Se cargó el nombre y apellido: ' + this.state.nombre + this.state.apellido);
        event.preventDefault();
    }

    render() {
        return(
            <form onSubmit={this.handleSubmit}>
                <label>
                    Nombre:
                    <input key="nombre" type="text" value={this.state.nombre} onChange={this.handleInputChange} />

                </label>
                <label>
                    Apellido
                    <input key="apellido" type="text" value={this.state.apellido} onChange={this.handleInputChange} /> 
                </label>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}

export default Form;
