import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header/Header';
import Login from './components/LoginForm/Login';
import Registro from './components/RegistrationForm/Registro';
import Home from './components/Home/Home';
import PrivateRoute from './utils/PrivateRoute';
import {
    BrowserRouter as Router,
    Switch,
    Route
} from 'react-router-dom';
import Alert from './components/AlertComponent/Alert';

function App() {
    const [title, updateTitle] = useState(null);
    const [errorMessage, updateErrorMessage] = useState(null);
    const redirectToLogin = () => {
      props.updateTitle('Login')
      props.history.push('./login');
  }
    return (
        <Router>
        <div className="App">
          <Header title={title}/>
            <div className="container d-flex align-items-center flex-column">
              <Switch>
                <Route path="/" exact={true}>
                  {redirectToLogin()}
                </Route>
                <Route path="/login" exact={true}>
                  <Login showError={updateErrorMessage} updateTitle={updateTitle}/>
                </Route>
                <Route path="/registro" exact={true}>
                  <Registro showError={updateErrorMessage} updateTitle={updateTitle}/>
                </Route>
                <PrivateRoute path="/home">
                  <Home/>
                </PrivateRoute>
              </Switch>
              <Alert errorMessage={errorMessage} hideError={updateErrorMessage}/>
            </div>
        </div>
        </Router>
    )
}

export default App;
