import React, {useState} from 'react';
import './Registro.css';
import {API_BASE_URL, ACCESS_TOKEN_NAME} from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";

function Registro(props) {
    const [state, setState] = useState({
        email : "",
        password : "",
        confirmPassword: "",
        successMessage: null
    })
    const handleChange = (e) => {
        const {id, value} = e.target
        setState(prevState => ({
            ...prevState,
            [id] : value
        }))
    }
    const { default: axios } = require("axios");

const sendDetailsToServer = () => {
    if(state.email.length && state.password.length) {
        props.showError(null);
        const payload={
            "email":state.email,
            "password":state.password,
        }

        axios.post(API_BASE_URL+'/user/register', payload).then(function (response) {
            if(response.status === 200) {
                setState(prevState => ({
                    ...prevState,
                    'successMessage': 'Se registró satisfactoriamente. Redireccionando a la página de inicio.'
                }))
                localStorage.setItem(ACCESS_TOKEN_NAME, response.data.token);
                redirectToHome();
                props.showError(null);
            } else {
                props.showError("Ocurrió un error");
            }
        })
        .catch(function (error) {
            console.log(error);
        });
    } else {
        props.showError('Por favor, ingresá un usuario y una contraseña válidos.')
    }
}
const redirectToHome = () => {
    props.updateTitle('Home')
    props.history.push('./home');
}
const redirectToLogin = () => {
    props.updateTitle('Login')
    props.history.push('./login');
}

    const handleSubmitClick = (e) => {
        e.preventDefault();
        if(state.password === state.confirmPassword) {
            sendDetailsToServer()
        } else {
            props.showError('Las contraseñas no coinciden');
        }
    }
    return (
        <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
            <form>
                <div className="form-group text-left">
                    <label htmlFor="exampleInputEmail1">Ingresa el email</label>
                    <input type="email" className="form-control" id="email" aria-describedby="emailHelp" placeholder="Ingresa un email" value={state.email} onChange={handleChange} />
                    <small id="emailHelp" className="form-text text-muted">Nunca compartiremos su correo</small>
                </div>
                <div className="form-group text-left">
                    <label htmlFor="exampleInputPassword1">Contraseña</label>
                    <input type="password" className="form-control" id="password" placeholder="Password" value={state.password} onChange={handleChange} />
                </div>
                <div className="form-group text-left">
                    <label htmlFor="exampleInputPassword1">Confirma tu contraseña</label>
                    <input type="password" className="form-control" id="confirmPassword" placeholder="Password" />
                </div>
                <button type="submit" className="btn btn-primary" onClick={handleSubmitClick}>Registrarse</button>
                <div className="LoginMensage">
                    <span>¿Tenes una cuenta? </span>
                    <span className="RegisterText" onClick={redirectToLogin}>Iniciá Sesión</span>
                </div>
            </form>
        </div>
    )
}
export default withRouter(Registro);
